","source":"**Data summaries**","metadata":{}},{"cell_type":"code","source":"# create a function for summary each column\n\nfor (i in 1: dim (data2020) [[2]])\n    {a <- names (data2020) [[i]]\n    b <- data2020 [[i]]\n    if (is.numeric (b) == TRUE)\n        {cat (\"Attribute Number\", i)\n        print (a)\n        print (summary (b))\n        print (\" \")}}","metadata":{"execution":{"iopub.status.busy":"2022-05-14T10:38:48.180778Z","iopub.execute_input":"2022-05-14T10:38:48.182403Z","iopub.status.idle":"2022-05-14T10:38:48.494221Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# summary all dataset data2020\n\nsummary (data2020)\nstr (data2020 [1, ]) # in R first number is one not zero","metadata":{"execution":{"iopub.status.busy":"2022-05-14T10:40:44.167073Z","iopub.execute_input":"2022-05-14T10:40:44.168868Z","iopub.status.idle":"2022-05-14T10:40:44.670657Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"library (rgdal)\nlibrary (sp)\n\nGR = read.csv ('../input/smartphone-decimeter-2022/test/2021-04-28-US-MTV-2/SamsungGalaxyS20Ultra/device_imu.csv')\nTGR_coord <- as.data.frame (x = GR$MeasurementX, y = GR$MeasurementY)\n\nplot (x = GR$MeasurementX, y = GR$MeasurementY)","metadata":{"execution":{"iopub.status.busy":"2022-05-14T12:01:10.53734Z","iopub.execute_input":"2022-05-14T12:01:10.583281Z","iopub.status.idle":"2022-05-14T12:01:30.2283Z"},"trusted":true},"execution_count":null,"outputs":[]}]}